#!/usr/bin/env node
import{Command as e}from"commander";import{ConvertCommand as o}from"./commands/ConvertCommand.js";let t=new e;t.command("convert").description("Converts .wav files to respective .bwav and patches .byml.").option("-f, --force","Overwrite existing files.",!1).option("-t, --keep-temps","Keep temporary files.",!1).option("-d, --debug","Enable debug mode.",!1).option("--copy <path>","Copies the file to the specified path.").action(o),t.parse();